import { AnimateSharedLayout, motion } from "framer-motion";
import React, { FunctionComponent } from "react";
import styles from '../styles/Play.module.css';
import Button from './Button'
import Image from 'next/image'

type PlayButtonProps = {
    advanced: boolean;
    onCLick: any;
};

const PlayButtons: FunctionComponent<PlayButtonProps> = ({ advanced, onCLick }) => {
    return (
        <AnimateSharedLayout>
            <motion.div className={styles.playbuttons}>
                {advanced ? (
                    <div className={styles.threegrid}>
                        <Button onClicK={() => onCLick(0)} classN={styles.sci} lid="sci">
                            <img src="/icon-scissors.svg" alt="" />
                        </Button>
                        <Button onClicK={() => onCLick(3)} classN={styles.spock}>
                            { " " }
                            <img src="/icon-spock.svg" alt="" />
                        </Button>
                        <Button onClicK={() => onCLick(1)} classN={styles.paper} lid="paper">
                            <img src="/icon-paper.svg" alt="" />
                        </Button>
                        <Button onClicK={() => onCLick(4)} classN={styles.liz}>
                            <img src="/icon-lizard.svg" alt="" />
                        </Button>
                        <Button onClicK={() => onCLick(2)} classN={styles.rock} lid="rock">
                            <img src="/icon-rock.svg" alt="" />
                        </Button>
                    </div>
                ) : (
                    <div className={styles.twogrid}>
                        <Button onClicK={() => onCLick(1)} classN={styles.sci2} lid="sci" >
                            {/* <img src="/icon-scissors.svg" alt="" /> */}
                            <Image
                                src="/icon-scissors.svg" alt="" width={100} height={100}
                            />
                        </Button>
                        <Button onClicK={() => onCLick(4)} classN={styles.paper2} lid="paper" >
                            <img src="/icon-paper.svg" alt="" />
                        </Button>
                        <Button onClicK={() => onCLick(2)} classN={styles.rock2} lid="rock" >
                            <img src="/icon-rock.svg" alt="" />
                        </Button>
                    </div>
                )}
            </motion.div>
        </AnimateSharedLayout>
    )
}

export default PlayButtons
