    event.preventDefault();
    setAdvancedMode(!advancedMode)
  }
  return (
    <div>
      <footer> 
        <div onClick={changeMode} className="modes">
            {advancedMode ? "Advanced" : "Normal"} Mode
        </div>
        <div className="rules">

        </div>
      </footer>
    </div>
  )
}
